def getContract(abi_file: json, address: str):
    address = Web3.toChecksumAddress(address)
    contract = w3.eth.contract(address=address, abi=abi_file)
    return contract

def signAndSend(tx):
    signed_txn = w3.eth.account.sign_transaction(tx, private_key=base.privateKey)
    print('signed')
    tx_token = w3.eth.send_raw_transaction(signed_txn.rawTransaction)
    print('Tx has been sent')
    w3.eth.wait_for_transaction_receipt(w3.toHex(tx_token))
    print(w3.toHex(tx_token))

def getBalance(token):
    contract = getContract(ERC20Abi, token)
    balance = contract.functions.balanceOf(base.address).call()
    return balance

def dexSwap(amount, token0, token1):
    UniRouter = getContract(uniAbi, router)
    basefee = w3.eth.gas_price
    chain_id = w3.eth.chain_id
    nonce = w3.eth.get_transaction_count(base.address)
    priorityfee = w3.toWei(1.5, 'gwei')

    tx_ = UniRouter.functions.swapExactTokensForTokens(
        w3.toWei(amount, 'ether'),
        0,
        [token0, token1],
        base.address
    )
    try:
        tx = tx_.buildTransaction(dict(
            type=2,
            nonce=nonce,
            maxFeePerGas=basefee + priorityfee,
            maxPriorityFeePerGas=priorityfee,
            gas=200000,
            chainId=chain_id
            ))
        print("Gas ", int(tx_.estimateGas({'from': base.address})))
        signAndSend(tx)
    except Exception as e:
        print(e)
        print('Swap would fail! Please check approval. Current Balance is: ', Web3.fromWei(getBalance(token0), 'gwei'))
        pass

